        static IEnumerable<TestCase<int, double>> GetPData(Data[] data)
        {
            return data.Select(d => TestCase.Create(d.N, d.X, d.LegendreP));
        }

        static IEnumerable<TestCase<int, double>> GetQData(Data[] data)
        {
            return data.Select(d => TestCase.Create(d.N, d.X, d.LegendreQ));
        }




        /// <summary>
        ///A test for LegendreP
        ///</summary>

        [TestMethod]
        public void LegendrePTest()
        {

            TestCaseSet<int, double>[] testCases = {
                new TestCaseSet<int, double>(_LegendrePSpots,            "Legendre P: Spots",        100),
                new TestCaseSet<int, double>(GetPData(_LegendrePQ),        "Legendre P: Small Values",    300),
                new TestCaseSet<int, double>(GetPData(_LegendrePQLarge),"Legendre P: Large Values",    600), // boost == 500
            };

            NumericFunctionTest.RunSet(Math2.LegendreP, "LegendreP", testCases);


        }

