        static IEnumerable<TestCase<double>> GetTanPIData(TanCotData[] data)
        {
            return data.Select(d => TestCase.Create(d.X, d.TanPI));
        }

        static IEnumerable<TestCase<double>> GetNegativeTanPIData(TanCotData[] data)
        {
            return data.Select(d => TestCase.Create(-d.X, -d.TanPI));
        }

        static IEnumerable<TestCase<double>> GetCotPIData(TanCotData[] data)
        {
            return data.Select(d => TestCase.Create(d.X, d.CotPI));
        }

        static IEnumerable<TestCase<double>> GetNegativeCotPIData(TanCotData[] data)
        {
            return data.Select(d => TestCase.Create(-d.X, -d.CotPI));
        }


        /// <summary>
        ///A test for TanPI
        ///</summary>
        [TestMethod]
        public void TanPITest()
        {

            TestCaseSet<double>[] testCases = {
                new TestCaseSet<double>(GetTanPIData(_TanCotPIData),"TanPI: Spot Data",    120,3),
                new TestCaseSet<double>(GetNegativeTanPIData(_TanCotPIData),"TanPI: Spot Data",    120,3),

                new TestCaseSet<double>(GetTanPIData(_TanCotPISmallData),"TanPI: Spot Data - Small",    120,3),
                new TestCaseSet<double>(GetNegativeTanPIData(_TanCotPISmallData),"TanPI: Spot Data - Small",    120,3),
                                              
                                             };

            NumericFunctionTest.RunSet(Math2.TanPI, "TanPI", testCases);

        }

        /// <summary>
        ///A test for CotPI
        ///</summary>
        [TestMethod]
        public void CotPITest()
        {

            TestCaseSet<double>[] testCases = {
                new TestCaseSet<double>(GetCotPIData(_TanCotPIData),"CotPI: Spot Data", 100,3),
                new TestCaseSet<double>(GetNegativeCotPIData(_TanCotPIData),"CotPI: Spot Data", 100,3),

                new TestCaseSet<double>(GetCotPIData(_TanCotPISmallData),"CotPI: Spot Data - Small", 100,3),
                new TestCaseSet<double>(GetNegativeCotPIData(_TanCotPISmallData),"CotPI: Spot Data - Small", 100,3),

                                              };

            NumericFunctionTest.RunSet(Math2.CotPI, "CotPI", testCases);

        }

